<?php
/**
 * @see https://github.com/WordPress/gutenberg/blob/trunk/docs/reference-guides/block-api/block-metadata.md#render
 */
global $post;

$prefix = 'd_';

$meta_output = array();
$terms_list = array('production','thematic','geography');
$thematics = array();
$geographys = array();

foreach($terms_list as $terms_name) {

	$terms = wp_get_post_terms( $post->ID, $terms_name);

	// Filter terms to return only top-level terms
	if ( $terms_name === 'thematic')
	$terms = array_filter($terms, function($term) {
		return $term->parent === 0;
	});

	if ( ! empty( $terms ) && ! is_wp_error( $terms ) ) {
		foreach ( $terms as $term ) {
			$term_link = get_term_link( $term );
			if ( ! is_wp_error( $term_link ) ) {
				if ($terms_name === 'thematic') { $t_general_color = get_term_meta($term->term_id, 't_general_color', true); $thematics[] = $term->slug; }
				if ($terms_name === 'geography') { 	$geographys[] = get_term_meta($term->term_id, 'g_special_code', true); }
				$meta_output[$terms_name] .= sprintf(
					'<div class="%s-list d-inline %s"><a href="%s" class="%s-item --link-disabled" %s>%s</a></div>',
					$terms_name,
					$terms_name === 'geography'?'inverse-list':'',
					esc_url( $term_link ),
					$terms_name,
					$terms_name === 'thematic'?'style="background-color:'.$t_general_color.';border-color:'.$t_general_color.';"':'',
					esc_html( sanitize_text_field($term->name) )
				);
			}
		}
	}
}

$_d_stage_opentostage   			= rwmb_get_field_settings( $prefix . 'stage_opentostage' );
$options_d_stage_opentostage 		= $_d_stage_opentostage['options'];
$d_stage_opentostage 				= rwmb_meta( $prefix . 'stage_opentostage', $post->ID); // Array ( [0] => visite_libre [1] => visite_collective )

$_d_stage_opentovisit  				= rwmb_get_field_settings( $prefix . 'stage_opentovisit' );
$options_d_stage_opentovisit 		= $_d_stage_opentovisit['options'];
$d_stage_opentovisit 				= rwmb_meta( $prefix . 'stage_opentovisit', $post->ID); // Array ( [0] => visite_libre [1] => visite_collective )

/* Contact form link */
$contact_slug = "contact";
$contact_page_object = get_page_by_path( $contact_slug );
$contact_permalink = get_permalink( $contact_page_object->ID );

// Get the previous post in the same category
$previous_post = get_adjacent_post(true, '', true, 'thematic');

// Get the next post in the same category
$next_post = get_adjacent_post(true, '', false, 'thematic');

if ( has_post_thumbnail($post->ID) ) { 
	$d_featured_img_id     		= get_post_thumbnail_id($post->ID);
	$d_featured_img_url_full 	= get_the_post_thumbnail_url($post->ID);
	foreach (array( 'thumbnail' ) as $size) { //'post-featured-image-s', 'post-featured-image-m'
		$d_featured_img_url = wp_get_attachment_image_src( $d_featured_img_id, $size ); // OK
		$d_featured_img_urls[$size] = ( !empty($d_featured_img_url[0]) )?$d_featured_img_url[0]:$d_featured_img_url_full; 
	}
	$d_alt = get_post_meta ( get_post_thumbnail_id(), '_wp_attachment_image_alt', true );
	$d_featured_img_caption = wp_get_attachment_caption($d_featured_img_id); // ADD WIL                    
	$d_thumb_img = get_post( $d_featured_img_id ); // Get post by ID
	$d_featured_img_description =  $d_thumb_img->post_content; // Display Description
}


